import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useRecipesContext } from "../hooks/useRecipesContext"

const EditRecipeForm = () => {
  // const [title, setTitle] = useState('');
  // const [category, setCategory] = useState('');
  // const [cookingTime, setCookingTime] = useState('');
  // const [ingredients, setIngredients] = useState('');
  // const [instructions, setInstructions] = useState('');
  // const [imageUrl, setImageUrl] = useState('');
  const { recipes, dispatch } = useRecipesContext()
  const [error, setError] = useState(null);
  //const [emptyFields, setEmptyFields] = useState([]);
  const navigate = useNavigate();
  const { id } = useParams();

  // const useFetch = (url) => {
  //   const [data, setData] = useState(null);
  //   const [error, setError] = useState(null);
  //   const [loading, setLoading] = useState(true);

  //   useEffect(() => {
  //     const fetchData = async () => {
  //       try {
  //         const response = await fetch(url);

  //         if (!response.ok) {
  //           throw new Error('Network response was not ok');
  //         }

  //         const result = await response.json();
  //         setData(result);
  //         setError(null);
  //       } catch (error) {
  //         setError('Error fetching data');
  //       } finally {
  //         setLoading(false);
  //       }
  //     };

  //     fetchData();
  //   }, [url]);

  //   return { data, error, loading };
  // };

  // const apiUrl = `http://localhost:3000/api/${id}`;
  // const { data: oldRecipe, error: fetchError, loading: fetchLoading } = useFetch(apiUrl);

  // useEffect(() => {
  //   // Update state values once data is available
  //   if (oldRecipe) {
  //     //dispatch({type: 'UPDATE_RECIPE', payload: oldRecipe})
  //     // setTitle(oldRecipe.title || '');
  //     // setCategory(oldRecipe.category || '');
  //     // setCookingTime(oldRecipe.cookingTime || '');
  //     // setIngredients(oldRecipe.ingredients || '');
  //     // setInstructions(oldRecipe.instructions || '');
  //     // setImageUrl(oldRecipe.imageUrl || '')
  //     dispatch({type: 'SET_RECIPES', payload: json})
  //   }
  // }, [oldRecipe]);

  useEffect(() => {
    const fetchWorkouts = async () => {
      const response = await fetch(`/api`)
      const json = await response.json()

      if (response.ok) {
        dispatch({type: 'SET_RECIPES', payload: json})
      }}
      fetchWorkouts()
       }, [dispatch])
      

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newRecipe = { title, category, cookingTime, ingredients, instructions, imageUrl };

    try {
      const response = await fetch(`/api/${id}`, {
        method: 'PATCH',
        body: JSON.stringify(newRecipe),
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const json = await response.json();

      if (!response.ok) {
        setError(json.error);
        //setEmptyFields(json.emptyFields);
      } else {
        // setEmptyFields([]);
        // setError(null);
        dispatch({type: 'UPDATE_RECIPE', payload: json})
        console.log('newRecipe updated:', json);
        const updatednewRecipeId = json._id;
        navigate(`/${updatednewRecipeId}`);
      }
    } catch (error) {
      setError('Error updating newRecipe');
    }
  };

  if (fetchLoading) {
    return <div>Loading...</div>;
  }

  if (fetchError) {
    return <div>Error fetching newRecipe data: {fetchError}</div>;
  }

  return (
    <form className="create" onSubmit={handleSubmit}>
      <h3>Edit Recipe</h3>

      <label>Recipe Title:</label>
      <input
        type="text"
        onChange={(e) => setTitle(e.target.value)}
        value={recipes.title}
        //className={emptyFields.includes('title') ? 'error' : ''}
      />

      {/* <label>Load (in kg):</label>
      <input
        type="number"
        onChange={(e) => setLoad(e.target.value)}
        value={load}
        className={emptyFields.includes('load') ? 'error' : ''}
      />

      <label>Number of Reps:</label>
      <input
        type="number"
        onChange={(e) => setReps(e.target.value)}
        value={reps}
        className={emptyFields.includes('reps') ? 'error' : ''}
      /> */}

      <label>Category:</label>
      
      <select
        onChange={(e) => setCategory(e.target.value)}
        value={recipes.category}
      >
        <option value="Breakfast">Breakfast</option>
        <option value="Main Course">Main Course</option>
        <option value="Soup">Soup</option>
        <option value="Salad">Salad</option>
        <option value="Dessert">Dessert</option>
      </select>


      <label>Cooking time:</label>
      <input 
        type="text" 
        onChange={(e) => setCookingTime(e.target.value)} 
        value={recipes.cookingTime}
        //className={emptyFields.includes('cookingTime') ? 'error' : ''}
      />

      <label>Ingredients:</label>
      <textarea 
        onChange={(e) => setIngredients(e.target.value)} 
        value={recipes.ingredients}
        //className={emptyFields.includes('title') ? 'error' : ''}
      ></textarea>

      <label> Instructions: </label>
      <textarea 
        onChange={(e) => setInstructions(e.target.value)} 
        value={recipes.instructions}
        //className={emptyFields.includes('instructions') ? 'error' : ''}
      ></textarea>

      <label>Image URL:</label>
      <input 
        type="text" 
        onChange={(e) => setImageUrl(e.target.value)} 
        value={recipes.imageUrl}
        //className={emptyFields.includes('imageUrl') ? 'error' : ''}
      />

      <button>Save</button>
      {error && <div className="error">{error}</div>}
    </form>
  );
};

export default EditRecipeForm;
