import { RecipesContext } from "../context/RecipesContext"
import { useContext } from "react"

export const useResipesContext = () => {
  const context = useContext(RecipesContext)

  if(!context) {
    throw Error('useResipesContext must be used inside an ResipesContextProvider')
  }

  return context
}
