import { useWorkoutsContext } from "../hooks/useWorkoutsContext"
import { useState } from 'react'
import { useNavigate } from "react-router-dom";

const WorkoutForm = () => {
  
  const [title, setTitle] = useState('');
  const [category, setCategory] = useState('');
  const [cookingTime, setCookingTime] = useState('');
  const [ingredients, setIngridients] = useState([]);
  const [instructions, setInstructions] = useState('');
  const [imageUrl, setImageUrl] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault()

    const workout = {title, category, cookingTime, ingredients, instructions, imageUrl}
    
    const response = await fetch('/api/workouts', {
      method: 'POST',
      body: JSON.stringify(workout),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    const json = await response.json()

    if (!response.ok) {
      setError(json.error)
      setEmptyFields(json.emptyFields)
    }
    if (response.ok) {
      setError('')
      setEmptyFields([])
      setTitle('')
      setCategory('')
      setCookingTime('')
      setIngredients([])
      setInstructions('')
      setImageUrl('')
      

      console.log('new recipe added:', json)
      dispatch({type: 'CREATE_WORKOUT', payload: json})
      //navigate(`/`);
      // Extract the id from the server response
      const newWorkoutId = json._id;
      console.log(newWorkoutId);
      navigate(`/api/workouts/${newWorkoutId}`);
      //navigate(`/api/workouts/:id`);
    }
  }

  const handleAddIngredient = () => {
    setIngredients = [...ingredients, ""];
  };

  return (
    <form className="create" onSubmit={handleSubmit}> 
      <h3>Add a New Recipe</h3>

      <label>Recipe title:</label>
      <input 
        type="text" 
        onChange={(e) => setTitle(e.target.value)} 
        value={title}
        className={emptyFields.includes('title') ? 'error' : ''}
      />

      <label>Category:</label>
      
      <select
        onChange={(e) => setCategory(e.target.value)}
        value={category}
      >
        <option value="Breakfast">Breakfast</option>
        <option value="Main Course">Main Course</option>
        <option value="Soup">Soup</option>
        <option value="Salad">Salad</option>
        <option value="Dessert">Dessert</option>
      </select>


      <label>Cooking time:</label>
      <input 
        type="text" 
        onChange={(e) => setTitle(e.target.value)} 
        value={cookingTime}
        //className={emptyFields.includes('cookingTime') ? 'error' : ''}
      />


      {/* <label>Ingredients:</label>
      {ingredients.map((ingredient, index) => (
      <input
            key={index}
            type="text"
            onChange={(e) => setIngredients([...ingredients, e.target.value,])}
            value={ingredient}
            className={emptyFields.includes('ingredients') ? 'error' : ''}
          /> */}

      <label> Instructions: </label>

      <input 
        type="text" 
        onChange={(e) => setInstructions(e.target.value)} 
        value={instructions}
        className={emptyFields.includes('instructions') ? 'error' : ''}
      />

      <label>Image URL:</label>
      <input 
        type="text" 
        onChange={(e) => setImageUrl(e.target.value)} 
        value={imageUrl}
        //className={emptyFields.includes('imageUrl') ? 'error' : ''}
      />
        
      {/* <button type="button" onClick={handleAddIngredient}>
        Add Ingredient
      </button> */}

      <label>Image URL::</label>
      <input 
        type="text" 
        onChange={(e) => setImageUrl(e.target.value)} 
        value={imageUrl}
        className={emptyFields.includes('imageUrl') ? 'error' : ''}
      />

       <button>Add Recipe</button>
      {error && <div className="error">{error}</div>}
    </form>
  )}

      //))};
